import java.util.*;
public class ArrayLispracticet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ArrayList<Integer> arrli = new ArrayList<Integer>(); 
		arrli.add(1);
		arrli.add(2);
		System.out.println(arrli);
		arrli.add(1,3);
		System.out.println(arrli);
		LinkedList<Integer> likndli = new LinkedList<Integer>(); 
		likndli.add(1);
		likndli.add(2);
		System.out.println(likndli);
		likndli.add(1,3);
		System.out.println(likndli);
		

	}

}
